package javastudy.week4.syntex;

public class WhileStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		int sum = 0;

		while (i < 10) {

			i++;
			sum += i;
			System.out.println("i : " + i);
			System.out.println("sum : " + sum + "\n");

		}
	}
}
